mapper.update:
update member
set mem_point = #{mem_point}
where mem_id = #{mem_id}

update member set
mem_exit = 2
where mem_id = #{mem_id}

update member set
mem_black = 2
where mem_id = #{mem_id}

update member set
mem_black = 1
where mem_id = #{mem_id}

update member set
mem_sit = #{mem_sit},
mem_shop = #{mem_shop},
mem_hair = #{mem_hair}
where mem_id = #{mem_id}

mapper.select:
select * from member
where mem_hp = #{mem_hp}
and mem_qet = #{mem_qet}
and mem_rep = #{mem_rep}
