mapper.update:
update hairshop
set has_score = #{has_score}
where has_id = #{has_id}

update bookmark
set bmk_fac='2'
where bmk_id=#{bmk_id}

mapper.select:
select *
from hairshop
where has_mem = #{mem_id}

mapper.update:
update hairboard
set hbrd_rep=#{hbrd_rep}
where hbrd_id=#{hbrd_id}

update hairres
set hres_stat=#{hres_stat},
	hres_spec=#{hres_spec}
where hres_id=#{hres_id}

update petstyle
set	pts_name = #{pts_name},
	pts_price = #{pts_price},
	pts_del = #{pts_del},
	pts_img = #{pts_img}
where pts_id = #{pts_id}

update member
set mem_hair=2
where mem_id=#{hsa_mem}
