mapper.select:
select * from
(select ROWNUM rnum,
A.*
from (select * from insProd where insp_del = 'N' order by to_number(insp_id) asc) A)
WHERE rnum between #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}

select * from
(select ROWNUM rnum,
A.*
from (select * from insProd where INSP_JOIN = #{petKind} and insp_del = 'N' order by to_number(insp_id) asc) A)
WHERE rnum between #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}

     select * from
              (select ROWNUM rnum,
                      A.*
               from  (select * from insProd where insp_del = 'N' order by to_number(insp_id) asc)A)
		 WHERE  rnum BETWEEN #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}
         and INSP_JOIN = #{petKind}
         and INSP_MINAGE <= ${petBirth}
         and INSP_MAXAGE >= ${petBirth}
         and INSP_SICK = #{petSick}

	select count(*) from insprod 
	 where INSP_JOIN = #{petKind}
       and INSP_MINAGE <= to_number(#{petBirth})
       and INSP_MAXAGE >= to_number(#{petBirth})
       and INSP_SICK = #{petSick}
       and insp_del = 'N'

			select * from insshopping , insprod
			 where inssp_insp = insp_id 
               and inssp_mem = #{inssp_mem}

select *
  from member
where mem_id = #{mem_id}

select * from insshopping
where inssp_mem = #{inssp_mem}
and inssp_insp= #{inssp_insp}

			select * from petkind a, animal b , mypet c
			where c.myp_petk = a.petk_id
            and a.petk_am = b.am_id
            and c.myp_petk = #{myp_petk}
            and c.myp_name = #{myp_name}
            order by petk_id asc

select * from insurance , insprod
where inssp_id = insp_id
and ins_stat = '완료'
and ins_dis= 'N'
and myp_id = #{myp_id}
and mem_id = #{mem_id}

mapper.update:
update insurance 
   set ins_dis = 'Y' 
 where ins_id = #{ins_id}

update insurance 
   set ins_stat = '신청취소' 
 where ins_id = #{ins_id}

mapper.select:
select * from insurance , insprod
where inssp_id = insp_id
and ins_stat = '완료'
and ins_dis= 'Y'
and myp_id = #{myp_id}
and mem_id = #{mem_id}

select * from insurance , insprod
where inssp_id = insp_id
and (ins_stat ='반려' or ins_stat ='신청취소')
and myp_id = #{myp_id}
and mem_id = #{mem_id}

mapper.update:
update insprod
   set insp_del = 'Y'
 where insp_id = #{insp_id}

update insprod
   set insp_del = 'N'
 where insp_id = #{insp_id}

 update insProd 
	set insp_fees =#{insp_fees} 
	,	insp_maxins =#{insp_maxins} 
	,	insp_minage =#{insp_minage} 
	,	insp_maxage =#{insp_maxage} 
	,	insp_period =#{insp_period} 
	,	insp_sick =#{insp_sick}  
  where insp_id=#{insp_id}

 update insurance
	set ins_stat = '완료'  
	, ins_start = #{ins_start}
	, ins_end = #{ins_end}
  where ins_id = #{ins_id}

 update insurance
	set ins_stat = '반려' 
	  , ins_start = #{ins_start} 
  where ins_id = #{ins_id}

mapper.select:
select * from insurance a , member b , account c
where a.mem_id = b.mem_id
and a.act_id = c.act_id
and a.mem_id =  #{mem_id}
and a.ins_id = #{ins_id}
and a.act_id = #{act_id}

mapper.update:
 update insurance
	set ins_dis = 'Y' 
  where ins_id = #{ins_id}

mapper.select:
select * 
  from insnotice
where inot_id = #{inot_id}

mapper.update:
update insnotice 
   set inot_title= #{inot_title}
     , inot_text= #{inot_text}  
 where inot_id = #{inot_id}

 update accident
	set accd_stat = '완료' 
	 , accd_pay = #{accd_pay}
	 , accd_insp = #{accd_insp}
  where accd_id = #{accd_id}

 update accident
	set accd_stat = '반려' 
	 , accd_res = #{accd_res}
  where accd_id = #{accd_id}
