mapper.select:
select * from
(select ROWNUM rnum,
A.*
from (select * from insProd order by to_number(insp_id) asc) A)
WHERE rnum between #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}

select * from
(select ROWNUM rnum,
A.*
from (select * from insProd where INSP_JOIN = #{petKind} order by to_number(insp_id) asc) A)
WHERE rnum between #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}

     select * from
              (select ROWNUM rnum,
                      A.*
               from  (select  *
                      from insProd
                      order by insp_id asc) A)
		 WHERE  rnum BETWEEN #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}
         and INSP_JOIN = #{petKind}
         and INSP_MINAGE <= ${petBirth}
         and INSP_MAXAGE >= ${petBirth}
         and INSP_SICK = #{petSick}

	select count(*) from insprod 
	 where INSP_JOIN = #{petKind}
       and INSP_MINAGE <= to_number(#{petBirth})
       and INSP_MAXAGE >= to_number(#{petBirth})
       and INSP_SICK = #{petSick}
