mapper.select:
select * 
from petsitter
where pst_id = #{pst_id}

select *
from petsitFaq
where psf_id = #{psf_id}

mapper.update:
update member
set mem_addr = #{mem_addr}, mem_addr2 = #{mem_addr2}
where mem_id = #{mem_id}

update mypet set
myp_del = 'Y'
where myp_id = #{myp_id}

mapper.select:
select count(*)
from sitterrev
where stv_pst = #{stv_pst}

mapper.update:
update sitterrev set
stv_text = #{stv_text}
where stv_id = #{stv_id}

update petsitfaq set
psf_text = #{psf_text}, psf_name = #{psf_name}, psf_date = sysdate, psf_mem = 'admin'
where psf_id = #{psf_id}

update petsitter set
pst_view = ${pst_view}
where pst_id = #{pst_id}

mapper.select:
select * 
from sitterApp
where sta_mem = #{sta_mem}

mapper.update:
update sitterapp set
sta_suc = 'Y'
where sta_id = #{sta_id}

update member set
mem_sit = 2
where mem_id = #{mem_id}

update sitterapp set
sta_file = #{sta_file}
where sta_id = #{sta_id}

mapper.select:
select count(*)
from petsitterchk
where pc_pst = #{pc_pst}
and pc_mem = #{pc_mem}

mapper.delete:
delete from sitterres
where str_pay = 'N' 
and str_date < #{str_date}

mapper.update:
update petsitter set
pst_chk = 'Y'
where pst_id = #{pst_id}
