mapper.select:
select * from ( select rownum as rnum,a.* from prod a where prod_id in (select pdd_prod from proddv where pdd_dvs in (${op0}))
and prod_id in (select pdd_prod from proddv where pdd_dvs in (${item}))
and prod_chk = 1 ) where rnum between #{pageVo.page} *#{pageVo.pageSize}-(#{pageVo.pageSize}-1) and #{pageVo.page}*#{pageVo.pageSize}

select * from prodoption
where prodo_prod = #{prod_id}
order by substr(prodo_id,7)

select * from ( select rownum as rnum,a.* from prod a where prod_id in (select pdd_prod from proddv where pdd_dvs= #{dvsVo.dvs_parent}) and prod_chk = 1 and prod_name like '%'||#{prod_name}||'%'
and prod_id in (select pdd_prod from proddv where pdd_dvs in (${item}))
) where rnum between ${pageVo.page} *${pageVo.pageSize}-(${pageVo.pageSize}-1) and ${pageVo.page}*${pageVo.pageSize}

mapper.update:
update prod
set prod_chk = 2
where prod_id = #{prod_id}

update prodrev
set prev_title = #{prev_title},
    prev_text = #{prev_text}
where prev_id = #{prev_id}

mapper.select:
select * from deliveryaddr
where daddr_mem = #{daddr_mem}
and daddr_chk = 1

select * from deliveryaddr
where daddr_mem = #{daddr_mem}
and daddr_chk = 3

mapper.update:
update prod
set prod_qty = #{prod_qty}
where prod_id = #{prod_id}

update deliveryaddr
set daddr_chk = 2
where daddr_id = #{daddr_id}

mapper.delete:
delete from recentprod
where recp_mem =#{recp_mem}
and recp_prod = #{recp_prod}

